mapper.insert:
INSERT INTO board
SET title = #{title}
,   contents = #{contents}
,   writer = #{writer}

mapper.update:
UPDATE board


        title = #{title}


        ,   contents = #{contents}


        ,   writer = #{writer}


WHERE board_id = #{boardId}

mapper.delete:
DELETE
FROM    board
WHERE   board_id = #{boardId}
    AND writer   = #{writer}
